import { useState } from 'react';
function AddEvent(props) {

    const [eventName, setEventName] = useState("");
    const [numberOfMembers, setNumberOfMembers] = useState("");
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [hours, setHours] = useState("");
    

    const setActive = props.setActive; 
    const events = props.events;
    const setEvents = props.setEvents;

    function handleSubmit(event) {
        event.preventDefault();
        addEvent();
    }

    function addEvent() {
        const newEvents = [
            ...events,
            {
                eventName: eventName,
                numberOfMembers: numberOfMembers,
                startDate: startDate,
                endDate: endDate,
                hours: hours
            }
        ];
        setEvents(newEvents);
        setActive("Profile");
    }
    
    return (
        <>
            <h2>Add an event!</h2>
            <form onSubmit={handleSubmit}>
                <div className="test">
                    <p>Name of event:</p> 
                    <input type = 'text' onChange={(e) => setEventName(e.target.value)} className="enter-field"/>
                </div>
                <div>
                    Total number of members: 
                    <input type = 'number' onChange={(e) => setNumberOfMembers(e.target.value)} className="enter-field"/>
                </div>
                <div>
                    Start Date:
                    <input type = 'datetime-local' onChange={(e) => setStartDate(e.target.value)} className="enter-field"/>
                </div>
                <div>
                    End Date:
                    <input type = 'datetime-local' onChange={(e) => setEndDate(e.target.value)} className="enter-field"/>
                </div>
                <div>
                    Total hours per slot:
                    <input type = 'number' onChange={(e) => setHours(e.target.value)} className="enter-field"/>
                </div>
                <input type="submit" value="Create" />
            </form>
            <div>
                <button onClick = {() => setActive("Profile")}> Return to your profile</button>
            </div>
            
        </>
    )
}
export default AddEvent;
