import { useState } from 'react';
function AddEvent(props) {

    const [eventName, setEventName] = useState("");
    const [numberOfMembers, setNumberOfMembers] = useState("");
    const [startDate, setStartDate] = useState("");
    const [endDate, setEndDate] = useState("");
    const [hours, setHours] = useState("");
    

    const setActive = props.setActive; 
    const events = props.events;
    const setEvents = props.setEvents;

    function handleSubmit(event) {
        event.preventDefault();
        addEvent();
    }

    function addEvent() {
        const newEvents = [
            ...events,
            {
                eventName: eventName,
                numberOfMembers: numberOfMembers,
                startDate: startDate,
                endDate: endDate,
                hours: hours
            }
        ];
        setEvents(newEvents);
        setActive("Profile");
    }
    
    return (
        <>
            <div className="event-page">
                <h2>Add an event!</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <p className="text-inline">Name of Event: </p> 
                        <input type = 'text' onChange={(e) => setEventName(e.target.value)} className="enter-field"/>
                    </div>
                    <div>
                        <p className="text-inline">Total Number of Members: </p> 
                        <input type = 'number' onChange={(e) => setNumberOfMembers(e.target.value)} className="enter-field"/>
                    </div>
                    <div>
                        <p className="text-inline">Start Date: </p> 
                        <input type = 'datetime-local' onChange={(e) => setStartDate(e.target.value)} className="enter-field"/>
                    </div>
                    <div>
                        <p className="text-inline">End Date: </p> 
                        <input type = 'datetime-local' onChange={(e) => setEndDate(e.target.value)} className="enter-field"/>
                    </div>
                    <div>
                        <p className="text-inline">Total Hours per Slot: </p> 
                        <input type = 'number' onChange={(e) => setHours(e.target.value)} className="enter-field"/>
                    </div>
                    <input type="submit" value="Create" className='enter-field'/>
                    <button onClick = {() => setActive("Profile")} className="enter-field"> Return to your profile</button>
                </form>
{/*                 <div>
                    <button onClick = {() => setActive("Profile")} className="enter-field"> Return to your profile</button>
                </div> */}
            </div>
        </>
    )
}
export default AddEvent;
