import React from 'react'
import {useState} from 'react';
import Calendar from 'react-calendar';


const time = ['Day Shift','Night Shift']


function Time_slots(props) {

  const [event, setEvent] = useState(null);
  const [info, setInfo] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [newTaskText, setNewTaskText] = useState("");

  function displayInfo(e) {
    setInfo(true);
    setEvent(e.target.innerText);
  }
  function handleAddTask(event) {
    event.preventDefault();
    addTask(newTaskText);
  }
  function addTask(description) {
    const newTasks = [
      ...tasks,
      {
        description: description,
        isComplete:false
      }
    ];
    setTasks(newTasks);
    console.log(newTasks);
  }

return (
    <div className="times">
      <form onSubmit={handleAddTask}>
        {time.map(times => {
          return (
          <div>
            <button onClick={(e)=> displayInfo(e)} value={newTaskText} onChange={(event) => setNewTaskText(event.target.value)}> {times} </button>
          </div>
              )
        })}
        <input type="submit" value="Add" />
      </form>

      <div>
        <p>You have selected {event} {props.date.toDateString()} </p>
      </div>
      <div>
        <p>Current List:</p>
       </div>
    </div>
  )
}

export default Time_slots;
