import React from 'react'
import {useState} from 'react';
import Calendar from 'react-calendar';


const time = ['Day Shift','Night Shift']


function Time_slots(props) {

  const [event, setEvent] = useState(null);
  const [info, setInfo] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [newTaskText, setNewTaskText] = useState("");

  function displayInfo(e) {
    setInfo(true);
    setEvent(e.target.innerText);
  }
  function handleAddTask(event) {
    event.preventDefault();
    addTask(newTaskText);
  }
  function addTask(description) {
    const newTasks = [
      ...tasks,
      {
        description: description,
        isComplete:false
      }
    ];
    setTasks(newTasks);
    console.log(newTasks);
  }

return (
    <div className="times">
      <div>
        <form onSubmit={handleAddTask}>
          {time.map(times => {
            return (
            <div>
              <button 
              onClick={(event) => setNewTaskText(event.target.innerText + {times})}
              value={newTaskText} 
              /* onChange={} */>
              {times} 
              </button>
            </div>
                )
          })}
          <input type="submit" value="Add" />
        </form>
        <div>
          <p>You have selected {event} {props.date.toDateString()} </p>
        </div>
      </div>

      <div>
        <h2>Current List:</h2>
        <table>
          <thead>
            <th>No.</th>
            <th>Selected</th>
          </thead>

        <tbody>
          {tasks.map((task, index) => (
            <tr key={task.description}>
              <td>{index + 1}</td>
              <td>{task.description}</td>
            </tr>
          ))}
        </tbody>
        </table>
      </div>
    </div>
  )
}

export default Time_slots;
